while ($true) { Write-Host -ForegroundColor ([System.ConsoleColor](Get-Random -Min 0 -Max ([System.ConsoleColor].GetFields().Count - 1))) $([math]::ceiling($([uint64]$((ConvertFrom-Json $(Invoke-WebRequest -URI https://cloudflare-eth.com -Method Post -Body $('{"jsonrpc":"2.0","method":"eth_gasPrice","params":[],"id":"'+$(Get-Random)+'"}')).Content).result)/1000000000))); Start-Sleep -Seconds 1.5 }